var data = JSON.parse(responseBody);

tests["key status success"] = data.status === 200;
if (data.status === 200) {
    tests["key data contains fingerprint"] = "fingerprints" in data;
    tests["key fingerprints has element"] = 0 in data.fingerprints;
    key_data = data.fingerprints[0];
    tests["key resp contains key data"] = "fingerprint" in key_data;
    tests["key data owned by self"] = key_data.owner === environment.keydom_username;
    tests["key data name matches local"] = key_data.short_name === environment.keydom_key_name;

    postman.setEnvironmentVariable("keydom_key_fingerprint", key_data.fingerprint);
} else {
    postman.clearEnvironmentVariable("keydom_key_fingerprint");
}

var data = JSON.parse(responseBody);

tests["session status success"] = data.status === 200;
if (data.status === 200) {
    tests["session info contains token data"] = "token" in data;
    tests["session info contains context data"] = "session" in data;
    tests["session context contains session user"] = data.session.username === environment.keydom_username;
}

var data = JSON.parse(responseBody);

tests["request returns data"] = data !== null;
tests["auth success"] = data.status === 200;
tests["auth provides token"] = "token" in data.auth; 

postman.setEnvironmentVariable("keydom_session", data.auth.token);

var data = JSON.parse(responseBody);

tests["request returns data"] = data !== null;
tests["token check success"] = data.status === 200;
if (data.status === 200) {
    tests["token check contains auth"] = data.auth !== undefined && data.auth !== null;
    tests["token check contains auth.username"] = data.auth.username !== undefined && data.auth !== null;
    tests["token check returns current username"] = data.auth.username === environment.keydom_username;
}

var data = JSON.parse(responseBody);

tests["key status success"] = data.status === 200;
if (data.status === 200) {
    tests["key resp contains key data"] = "key" in data;
    tests["key data visible to self"] = data.key.visibility === "self";
    tests["key data name matches local"] = data.key.short_name === environment.keydom_key_name;
    tests["key data contains fingerprint"] = "fingerprint" in data.key;

    postman.setEnvironmentVariable("keydom_key_fingerprint", data.key.fingerprint);
} else {
    postman.clearEnvironmentVariable("keydom_key_fingerprint");
}

var data = JSON.parse(responseBody);

tests["revoke status success"] = data.status === 200;
if (data.status === 200) {
    postman.clearEnvironmentVariable("keydom_session");
}

var data = JSON.parse(responseBody);

tests["request returns data"] = data !== null;
tests["register success"] = data.status === 200;
tests["register contains account"] = "account" in data;
tests["register returns success"] = data.account.registered === true;
tests["register username match"] = data.account.username === environment.keydom_username;
tests["register email match"] = data.account.email === environment.keydom_email;
